package main

import (
	"errors"
	"fmt"
)

func main() {

	/* tipo int :
	int = o sistema reconhece de arcordo com a arquitetura do computador
	int8 = aceita ate 8 bytes
	int16 = aceita ate 16 bytes
	int32 = aceita ate 32 bytes
	int64 = aceita ate 64 bytes
	int aceita numeros negativos
	*/
	// var int8 int8 = 1111 || errado nao suporta o valor | cannot use 1111 (untyped int constant) as int8 value in variable declaration (overflows)
	var int8 int8 = 111
	var int16 int16 = 22222
	var int32 int32 = 333333333
	var int64 int64 = 4444444444444444444
	var int int = 5555555555555555555
	fmt.Println("Int8 : ", int8)
	fmt.Println("Int16 : ", int16)
	fmt.Println("Int32 : ", int32)
	fmt.Println("Int64 : ", int64)
	fmt.Println("Int : ", int)
	println()

	/*
			uint = unsygned int || int sem sinal, ele nao aceita numeros negativos | sinal
			var uint81 uint8 = -111 variavel com erro = cannot use -111 (untyped int constant) as uint8 value in variable declaration (overflows)
		var uint8 uint8 = 99
		var uint16 uint16 = 8888
		var uint32 uint32 = 777777777
		var uint64 uint64 = 6666666666666666666
		var uint uint = 5555555555555555555

	*/
	// var uint81 uint8 = -111 variavel com erro = cannot use -111 (untyped int constant) as uint8 value in variable declaration (overflows)
	var uint8 uint8 = 99
	var uint16 uint16 = 8888
	var uint32 uint32 = 777777777
	var uint64 uint64 = 6666666666666666666
	var uint uint = 5555555555555555555
	fmt.Println("uInt8 : ", uint8)
	fmt.Println("uInt16 : ", uint16)
	fmt.Println("uInt32 : ", uint32)
	fmt.Println("uInt64 : ", uint64)
	fmt.Println("uInt : ", uint)
	println()

	/*
		Alias
		Olhar na Docyumentação pois existem outro

		int32 = rune | ex :  var numero3 rune = 12456
		uint8 = byte | ex :  var numero4 byte = 123
	*/
	var numero3 rune = 12456
	var numero4 byte = 123
	fmt.Println("Alias int32 = rune : ", numero3)
	fmt.Println("Alias int32 = rune : ", numero4)
	println()

	// numeros Reais so tem 2 tipos float32 e float64 | a diferença e que esses numeros aceita valores quebrados (valores com . ou , )
	var numero5 float32 = 99999999999999999999999999999999999999.
	var numero6 float64 = 99999999999999999999999999999999999988.
	numero7 := 99999999999999999999999999999999999988.
	fmt.Println("Alias float32 : ", numero5)
	fmt.Println("Alias float64 : ", numero6)
	fmt.Println("Alias float : ", numero7)
	println()

	//no go não tem o tipo char no go o char e subustituido por um numero
	var str1 string = "Texto 1"
	str2 := "Texto 2"
	fmt.Println("Str 1 :", str1)
	fmt.Println("Str 2 :", str2)
	println()

	//mais proximo de char no go || devolve o numero do caractere da tabela ASCII
	// char := 'AA" || nao deixa colocar mais que um caracter || illegal rune literalsyntax
	char1 := 'w'
	char2 := 'A'
	fmt.Println("caractere da tabela ASCII 1 :", char1)
	fmt.Println("caractere da tabela ASCII 2 :", char2)

	// Valor zero || e o valor atribuido em uma variavel quando não iniciamos ela (valor que ela chia automaticamente)
	var texto1 float32
	var texto2 float64
	var texto3 string
	var texto4 rune
	var texto5 byte
	var texto6 bool
	var texto7 error
	fmt.Println("Valor float32 quando não declarado :", texto1)
	fmt.Println("Valor float64 quando não declarado :", texto2)
	fmt.Println("Valor string quando não declarado :", texto3)
	fmt.Println("Valor float32 quando não declarado :", texto4)
	fmt.Println("Valor float32 quando não declarado :", texto5)
	fmt.Println("Valor booleano quando não declarado :", texto6)
	fmt.Println("Valor error quando não declarado :", texto7)
	println()

	// tipo booleano se retorna true ou false
	var booleano bool
	fmt.Println("Valor Booleano é :", booleano)
	println()

	// tipo error || tratamento de erro no go e mto diferente dos outro
	var erro1 error
	fmt.Println("Error :", erro1)
	// para colocar um valor dentro de erro
	var erro2 error = errors.New("Erro Interno")
	fmt.Println("Error :", erro2)

}
